mapper.select:
SELECT COUNT(id) FROM blog_file 
WHERE
	file_parent = #{parent}

		AND file_type = #{type,javaType=BlogFileType,typeHandler=EnumOrdinalTypeHandler}

SELECT bf.id, file_name, file_type, file_createDate, file_lft, file_rgt, file_parent , bcf.id AS cf_id, file_extension, file_size, file_store, file_server, file_originalname, file_width, file_height, file_path FROM blog_file bf LEFT OUTER JOIN blog_common_file bcf ON bcf.id = bf.common_file WHERE file_parent = #{parent}
AND file_type = #{type,javaType=BlogFileType,typeHandler=EnumOrdinalTypeHandler}
ORDER BY file_createDate DESC,id DESC LIMIT #{offset},#{pageSize}

mapper.update:
UPDATE blog_file 


		file_lastmodifydate = #{lastModifyDate},


		file_name = #{name}


WHERE id = #{id}

mapper.delete:
DELETE FROM blog_file 
WHERE 
file_lft BETWEEN #{lft} AND #{rgt}

mapper.update:
UPDATE blog_file SET file_lft = 0-file_lft, file_rgt = 0-file_rgt WHERE file_lft >= #{src.lft} AND file_rgt <= #{src.rgt};
UPDATE blog_file SET file_lft = file_lft - #{src.width} WHERE file_lft > #{src.rgt};
UPDATE blog_file SET file_rgt = file_rgt - #{src.width} WHERE file_rgt > #{src.rgt};
UPDATE blog_file SET file_lft = file_lft + #{src.width} WHERE file_lft >= IF(#{parent.rgt} > #{src.rgt}, #{parent.rgt} - #{src.width}, #{parent.rgt});
UPDATE blog_file SET file_rgt = file_rgt + #{src.width} WHERE file_rgt >= IF(#{parent.rgt} > #{src.rgt}, #{parent.rgt} - #{src.width}, #{parent.rgt});
UPDATE blog_file SET file_lft = 0-file_lft+IF(#{parent.rgt} > #{src.rgt}, #{parent.rgt} - #{src.rgt} - 1, #{parent.rgt} - #{src.rgt} - 1 + #{src.width}), file_rgt = 0-file_rgt+IF(#{parent.rgt} > #{src.rgt}, #{parent.rgt} - #{src.rgt} - 1, #{parent.rgt} - #{src.rgt} - 1 + #{src.width}) WHERE file_lft <= 0-#{src.lft} AND file_rgt >= 0-#{src.rgt};
UPDATE blog_file SET file_parent = #{parent.id} WHERE id = #{src.id};
