	INSERT INTO blog_comment(parent_id,path,content,user_id,article_id)
	VALUES(#{parent.id},#{path},#{content},#{user.id},#{article.id})



	SELECT COUNT(id) FROM blog_comment WHERE  path LIKE CONCAT('', #{path},'%')



	DELETE FROM blog_comment  WHERE  path LIKE CONCAT('', #{path},'%')



	DELETE FROM blog_comment WHERE id = #{id}
