mapper.select:
SELECT a.id,a.parent_id,a.parent_path,a.content,a.user_id,a.article_id,a.comment_date,bou.avatar,bou.nick_name,comment_date FROM blog_comment a JOIN (SELECT id, parent_path FROM blog_comment WHERE parent_path = '/' AND article_id = #{article.id}
ORDER BY comment_date ASC,id ASC
ORDER BY comment_date DESC,id DESC
LIMIT #{offset},#{pageSize}) roots ON (a.parent_path LIKE CONCAT('/',roots.id,'/%') OR a.id=roots.id) LEFT OUTER JOIN blog_oauth_user bou ON a.`user_id` = bou.id;
