mapper.select:
SELECT id,oauth_id,avatar,nick_name,oauth_type,oauth_status,register_date FROM blog_oauth_user 
WHERE 
		oauth_id = #{oauthid} 
	AND 
		oauth_type = #{oauthType,javaType=OauthType,typeHandler=EnumOrdinalTypeHandler}

SELECT id,oauth_id,avatar,nick_name,oauth_type,oauth_status,register_date FROM blog_oauth_user
nickname LIKE CONCAT('%', #{nickname},'%')
ORDER BY id DESC LIMIT #{offset},#{pageSize}

mapper.update:
UPDATE blog_oauth_user
SET 
	avatar = #{avatar},
	nick_name = #{nickname},

		oauth_status = #{status,javaType=OauthUserStatus,typeHandler=EnumOrdinalTypeHandler}

WHERE
		oauth_id = #{oauthid} 
	AND 
		oauth_type = #{type,javaType=OauthType,typeHandler=EnumOrdinalTypeHandler}
