mapper.select:
SELECT id, fragement_name, fragement_description, fragement_tpl, fragement_create_date, space_id, is_global FROM blog_fragement_user WHERE fragement_name = #{name}
AND space_id IS NULL
AND space_id = #{space.id}

SELECT bfu.id, fragement_name, fragement_description, fragement_tpl, fragement_create_date, space_id, space_name, is_global FROM blog_fragement_user bfu LEFT OUTER JOIN blog_space bs ON bfu.space_id = bs.id
fragement_name LIKE CONCAT('%', #{name},'%')
AND space_id = #{space.id}
AND is_global = #{global}
LIMIT #{offset},#{pageSize}

SELECT COUNT(id) FROM blog_fragement_user
fragement_name LIKE CONCAT('%', #{name},'%')
AND space_id = #{space.id}
AND is_global = #{global}

mapper.update:
UPDATE blog_fragement_user
fragement_name = #{name},
fragement_description = #{description},
fragement_tpl = #{tpl},
space_id = #{space.id}, is_global = #{global}
WHERE id = #{id}
